import { Component, OnInit } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { Movies } from 'src/app/classes/movies';
import { MovieServiceService } from 'src/app/services/movie-service.service';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-admin-dashboard',
  templateUrl: './admin-dashboard.component.html',
  styleUrls: ['./admin-dashboard.component.css']
})
export class AdminDashboardComponent implements OnInit{

  id: number;
  movieObs: Observable<Movies[]>;
  isMovieAdded = false;
  isMovieUpdated = false;
  movies = Movies;

  constructor(private route: ActivatedRoute,  private router: Router, private movieService: MovieServiceService) { }

  ngOnInit() {
    this.reloadMovies();
  }

  reloadMovies() {
    this.movieObs = this.movieService.getAllMovies();
  }
  //Add Movie to Database
  createMovie() {
    this.movieService.createMovie(this.movies)
      .subscribe(data => {
        console.log(data);
        this.isMovieAdded = true;
      });
  }

  //updateMovie() {
    //this.movieService.updateMovie(this.id, this.movies)
      //.subscribe(data => {
        //console.log(data);
        //this.movies = new Movies();
    //  });
  //}

  // Get Movies
  getMoviesById(id: number) {
    this.movieService.getMoviesById(id)
      .subscribe(data => {
        console.log(data);
      })
    }    




    deleteMovie(id:number){
      this.movieService.deleteAMovie(id)
      .subscribe(data => {
        console.log(data);  
        this.reloadMovies();
      });
 }

    }
